//Initializing
var i = 0;
var images = []; //array
var time = 3000; // time in millie seconds

//images

images[0] = "url('images/bg01.jpg')";
images[1] = "url('images/bg02.jpg')";
images[2] = "url('images/bg03.jpg')";
//function

function changeImage() {
    // var el = document.getElementById('body');
    document.body.style.backgroundImage = images[i];
    if (i < images.length - 1) {
        i++;
    } else {
        i = 0;
    }
    setTimeout('changeImage()', time);
}

window.onload = changeImage;
